import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Navbar from './Components/Navbar';
import Mapa from './Components/Mapa';

function App() {
  const [mensagem, setMensagem] = useState('');

  useEffect(() => {
    axios.get('/api/hello')
      .then(res => setMensagem(res.data.message))
      .catch(err => console.error(err));
  }, []);

  const panelStyle = {
    border: '1px solid #ccc',
    borderRadius: '8px',
    padding: '10px',
    margin: '10px',
    background: '#f9f9f9',
    width: '100%',
    maxWidth: '500px',
  };

  const containerStyle = {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'center',
    gap: '20px',
    padding: '20px',
  };

  return (
    <div>
      <Navbar />
      <main style={{ padding: '80px 20px' }}>
        <h1>Frontend React</h1>
        <p>{mensagem}</p>

        {/* Map Section */}
        <h2>Mapa</h2>
        <Mapa />

        {/* Gráficos do Grafana */}
        <h2 style={{ textAlign: 'center', marginTop: '40px' }}>Dados Meteorológicos</h2>
        <div style={containerStyle}>
          <div style={panelStyle}>
            <h3 style={{ textAlign: 'center' }}>Temperatura</h3>
            <iframe
              src="https://andrefilipe200312.grafana.net/d-solo/7fd7b9c0-b4fd-4b67-b5d4-641cd0416cbb/temperatura?orgId=1&from=now-6h&to=now&timezone=browser&panelId=3"
              width="100%"
              height="200"
              frameBorder="0"
              title="Temperatura"
            />
          </div>

          <div style={panelStyle}>
            <h3 style={{ textAlign: 'center' }}>Pluviosidade</h3>
            <iframe
              src="https://andrefilipe200312.grafana.net/d-solo/7fd7b9c0-b4fd-4b67-b5d4-641cd0416cbb/temperatura?orgId=1&from=now-6h&to=now&timezone=browser&panelId=2"
              width="100%"
              height="200"
              frameBorder="0"
              title="Pluviosidade"
            />
          </div>

          <div style={panelStyle}>
            <h3 style={{ textAlign: 'center' }}>Humidade</h3>
            <iframe
              src="https://andrefilipe200312.grafana.net/d-solo/7fd7b9c0-b4fd-4b67-b5d4-641cd0416cbb/temperatura?orgId=1&from=now-6h&to=now&timezone=browser&panelId=1"
              width="100%"
              height="200"
              frameBorder="0"
              title="Humidade"
            />
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
